import React from 'react';
import { View, Text, FlatList, Image, TouchableOpacity, Dimensions, SafeAreaView } from 'react-native';
import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import CustomHeaderButton from '../components/CustomHeaderButton';
import { PRODUCTS, FRESH_FINDS } from '../data/dummy-data';
import Card from '../components/Card';
import ImageCarousel from '../components/ImageCarousel';
import ProductItem from '../components/ProductItem';

const width = Dimensions.get('window').width;
const height = width * 0.6;

const saleUrl = [
    'https://woocommerce.com/wp-content/uploads/2012/08/woocommerce-smart-coupons-header-banner-900px.png',
    'data:image/jpeg;base64,',
    'https://previews.123rf.com/images/ikonstudio/ikonstudio1711/ikonstudio171100004/89556609-linear-discount-banner-concept-sale-off-sales-and-discounts-promotion-banner-retro-colors-and-shadow.jpg'
];

const HomeScreen = props => {
    const categories = [];
    PRODUCTS.map(prod => {
        var unique = categories.find(x => x.catName === prod.catName);
        if (!unique) {
            categories.push(prod);
        }
    });
    return (
        <SafeAreaView style={{ margin: 2, flex: 1 }}>
            <ImageCarousel
                imageUrls={saleUrl}
                width={width}
                height={height}
            />
            <Card style={{ padding: 10, marginVertical: 10 }}>
                <View style={{ flexDirection: 'row', justifyContent: 'space-between', marginBottom: 10 }}>
                    <Text style={{ fontSize: 20, fontWeight: 'bold' }}>Explore Categories</Text>
                    <TouchableOpacity onPress={() => { props.navigation.navigate('Categories') }}>
                        <Text style={{ color: '#444746', alignSelf: 'center' }}>See All{'>>'}</Text>
                    </TouchableOpacity>
                </View>
                <FlatList
                    keyExtractor={item => item.id}
                    horizontal
                    showsHorizontalScrollIndicator={false}
                    data={categories}
                    renderItem={(itemData) => {
                        return (
                            <TouchableOpacity
                                onPress={() => { props.navigation.navigate('Category', { title: itemData.item.catName }) }}
                                style={{ marginRight: 5 }}
                            >
                                <Image
                                    source={{ uri: itemData.item.iconUrl }}
                                    style={{ width: 52, height: 52, alignSelf: 'center', marginBottom: 5 }}
                                />
                                <Text style={{ fontSize: 16 }}>{itemData.item.catName}</Text>
                            </TouchableOpacity>
                        );
                    }}
                />
            </Card>
            <SafeAreaView style={{ flex: 1, marginBottom: 2 }}>
                <Card style={{ padding: 10 }}>
                    <Text style={{ fontSize: 20, fontWeight: 'bold', marginBottom: 5 }}>Fresh Finds</Text>
                    <FlatList
                        keyExtractor={item => item.id}
                        data={FRESH_FINDS}
                        numColumns={2}
                        renderItem={(itemData) => {
                            return (
                                <ProductItem
                                    navigation={props.navigation}
                                    imageUrls={itemData.item.imageUrls}
                                    catName={itemData.item.catName}
                                    subName={itemData.item.subName}
                                    name={itemData.item.name}
                                    price={itemData.item.price}
                                    id={itemData.item.id}
                                />
                            );
                        }}
                    />
                </Card>
            </SafeAreaView>
        </SafeAreaView>
    );
};

export const screenOptions = navData => {
    return {
        headerRight: () => (
            <HeaderButtons HeaderButtonComponent={CustomHeaderButton}>
                <Item
                    title="Like"
                    iconName={Platform.OS === 'android' ? 'md-heart-outline' : 'ios-heart-outline'}
                    onPress={() => {
                        navData.navigation.navigate('LikedItems');
                    }}
                />
            </HeaderButtons>
        )
    };
};

export default HomeScreen;